% read the transformations file to one big matrixfile = dlmread("odometry8.txt");tickToMeter = 0.000085292090497737556558; % [m/tick]x = 0;  % [m]y = 0;  % [m]gx = 0;gy = 0;theta = 0; % [rad]gyroTheta = 0;initialtheta = 0;b = 0.23; % wheelbase d [m]XX=[]YY=[]GXX=[]GYY=[]THETATHETA=[]GYROTHETA=[]for row_index = 1:1:size(file,1) 	    if row_index == 1      initialtheta = theta;      end    	  timestamp = file(row_index, 1);    gTheta = 0; %file(row_index, 4);  	  dLeft = file(row_index, 2);	  dRight = file(row_index, 3);    mLeft = dLeft * tickToMeter;    mRight = dRight * tickToMeter;      displacement = (mRight + mLeft)/2;    rotation = (mLeft - mRight)/b; % or gTheta        		x = x + displacement * cos(theta + rotation / 2);  	y = y + displacement * sin(theta + rotation / 2);    theta = theta + rotation;    gyroTheta = gyroTheta + gTheta;    gx = gx + displacement * cos(gyroTheta + gTheta / 2);    gy = gy + displacement * sin(gyroTheta + gTheta / 2);    GXX(end + 1) = gx;    GYY(end + 1) = gy;          		XX(end + 1) = x;    YY(end +1) = y;    THETATHETA(end + 1) = theta;        GYROTHETA(end + 1) = gyroTheta;        end    plot(THETATHETA,"b")hold on%plot(GYROTHETA,"r.")print -djpg thetafigure 2axis equalplot(XX,YY,"b")hold on%plot(GXX,GYY,"r.")